import React, {useEffect} from 'react';
import './App.css';
import {useDispatch,useSelector} from 'react-redux';
import {NoteLine} from './NoteLine';
import {UserBox} from './UserBox';
import {loadLine, endEdit, startEdit} from './LineActions';
import {beginLogin,beginNewNoteSheet}  from './UserActions';
import {store} from './LineStore';
import MathJax from 'react-mathjax';

function App() {

  window.addEventListener('keydown',function(e){if(e.keyIdentifier=='U+0008'||e.keyIdentifier=='Backspace'||e.keyCode==8){if(e.target==document.body){e.preventDefault();return false;}}},true);

  const noteLines = useSelector(state => state.noteLines);
  const lineCount = useSelector(state => state.lineCount);
  const currentUserName = useSelector(state => state.userName);
  const noteSheetList = useSelector(state => state.noteSheets);
  const currentNoteSheet_id = useSelector(state => state.noteSheet_id);
  const dispatch = useDispatch();

  useEffect(() =>{
    dispatch(loadLine({lineNumber: 1, lineContents: ""}));
    dispatch(startEdit(1));
  },[dispatch]);

  //console.log("Pre-sort:" + JSON.stringify(noteLines))
  //console.log(store.getState())
  //const sortedLines = Object.values(noteLines).sort((a,b) => a.lineNumber - b.lineNumber);
  //console.log("Post-sort:" + JSON.stringify(noteLines))

  return (
    <div className="App">

      <div className="header">
        <div className="title"> Note-Tex </div>
      </div>

      <div className="middle">

        <div className="leftMargin">
          <div className="tipBox">
            <div className="tipTitle">
              Tips
            </div>
            <div className="tipBox">
              <div className="tipHeading">
                Bold
              </div>
              %text% -> <b>text</b>
            </div>
            <div className="tipBox">
              <div className="tipHeading">
                Italic
              </div>
              `text` -> <i>text</i>
            </div>
            <div className="tipBox">
              <div className="tipHeading">
                LaTex
              </div>
                $f(x)=x^22$ -> <MathJax.Provider><MathJax.Node inline formula={'f(x)=x^2'} /></MathJax.Provider>
            </div>
          </div>
        </div>

          <div className="body">
           {noteLines.map(line => <NoteLine key={line.lineNumber + line.lineContents} lineCount={lineCount} noteLine={line} userName={currentUserName} noteSheet_id={currentNoteSheet_id} noteLines={noteLines}/>)}
        </div>

        <div className="rightMargin">
          <UserBox userName={currentUserName} currentNoteSheet_id={currentNoteSheet_id}/>
        </div>

      </div>

    </div>
  );
}

export default App;
