import {useState} from 'react';
export const Action = Object.freeze({
  BeginEdit: 'BeginEdit',
  EndEdit: 'EndEdit',
  LoadLine: 'LoadLine',
})

export function beginEdit(noteLine){
  return{
    type: Action.BeginEdit,
    payload: noteLine
  };
}

export function endEdit(noteLine){
   useState(noteLine.lineContents);
  return{
    type: Action.EndEdit,
    payload: noteLine,
  };
}

export function loadLine(noteLine){
  return {
    type: Action.LoadLine,
    payload: noteLine,
  };
}

