import React, {useState, getState} from 'react';
import {useSelector,useDispatch} from 'react-redux';
import {checkForErrors} from './UserActions'

export const LineAction = Object.freeze({
  StartEdit: 'StartEdit',
  EndEdit: 'EndEdit',
  LoadLine: 'LoadLine',
  IncrementLineCount: 'IncrementLineCount',
  AddNewLine:'AddNewLine',
  RemoveLine:'removeLine'
})

const host = 'http://websystems.senapatiratne.com:1443';


export function startEdit(lineNumber){
  return{
    type: LineAction.StartEdit,
    payload: lineNumber
  };
}

export function beginEndEdit(noteLine, noteLines, currentLineCount, currentNoteSheet_id,currentUserName){
  if(currentUserName==null || currentUserName===""){
    return dispatch => {
      dispatch(finishEndEdit(noteLine))
    }
  }
  const compiledNoteLines = noteLines.map(line =>{
       if(line.lineNumber === noteLine.lineNumber){
         return noteLine;
       }
       else{
         return line;
       }
     });
  return dispatch => {
    var temp = JSON.stringify(compiledNoteLines)
    const newBody = {editedLine:JSON.stringify(noteLine),lineCount:currentLineCount,noteSheet_id:currentNoteSheet_id,userName:currentUserName};
    alert("Newbody:"+JSON.stringify(newBody))
    const options = {method: 'PATCH', headers:{'Content-Type': 'application/json'}, body:(newBody)};
    alert("options:"+JSON.stringify(options))
    return dispatch => {
      fetch(`${host}/noteSheetEdit`,options).then(checkForErrors)
        .then(responce => responce.json())
        .then(data => {
          if(data.ok){
            dispatch(finishEndEdit(noteLine))
          }
        })
        .catch(e => console.error(e));
    };
  };
  //dispatch(finishEndEdit(noteLine))
}

export function finishEndEdit(noteLine){
  return{
    type: LineAction.EndEdit,
    payload: noteLine,
  };
}

export function addNewLine(location){
    return{
    type: LineAction.AddNewLine,
    payload: location,
  }
}

export function loadLine(noteLine){
  return {
    type: LineAction.LoadLine,
    payload: noteLine,
  };
}

export function incrementLineCount(){
  return{
    type: LineAction.IncrementLineCount,
    payload: null
  }
}

export function removeLine(noteLine){
  return{
    type: LineAction.RemoveLine,
    payload: noteLine
  }
}
