import React, {useState, getState} from 'react';
import {useSelector,useDispatch} from 'react-redux';

export const LineAction = Object.freeze({
  StartEdit: 'StartEdit',
  EndEdit: 'EndEdit',
  LoadLine: 'LoadLine',
  IncrementLineCount: 'IncrementLineCount',
  AddNewLine:'AddNewLine',
  RemoveLine:'removeLine'
})

export function startEdit(lineNumber){
  return{
    type: LineAction.StartEdit,
    payload: lineNumber
  };
}

export function beginEndEdit(noteLine, noteLines){
  const compiledNoteLines = noteLines.map(line =>{
       if(line.lineNumber === noteLine.lineNumber){
         return noteLine;
       }
       else{
         return line;
       }
     });
  alert(JSON.stringify(compiledNoteLines))
  return dispatch => {
      /*fetch(`${host}/noteSheetList/${userName}`)
        .then(checkForErrors)
