import {useDispatch} from 'react-redux';
export const Action = Object.freeze({
  BeginEdit: 'BeginEdit',
  EndEdit: 'EndEdit',
  LoadLine: 'LoadLine',
  IncrementLineCount: 'IncrementLineCount',
  AddNewLine:'AddNewLine'
})

export function beginEdit(noteLine){
  return{
    type: Action.BeginEdit,
    payload: noteLine
  };
}

export function endEdit(noteLine, lineCount){

  if(noteLine.lineNumber === lineCount){
    dispatch(addNewLine(lineCount))
  }

  return{
    type: Action.EndEdit,
    payload: noteLine,
  };
}

export function addNewLine(lineCount){
  return{
    type: Action.AddNewLine,
    payload: lineCount,
  }
}

export function loadLine(noteLine){
  return {
    type: Action.LoadLine,
    payload: noteLine,
  };
}

export function incrementLineCount(){
  return{
    type: Action.IncrementLineCount,
    payload: null
  }
}

