import React, {useState} from 'react';
import {useDispatch} from 'react-redux';
import {beginEdit, endEdit} from './LineActions'

export function NoteLine(props){
  const noteLine = props.noteLine;
  const dispatch = useDispatch();
  const [lineContents, setLineContents] = useState(noteLine.lineContents);

  const onBeginEdit = () =>{
    dispatch(beginEdit(noteLine));
  }

  const onEndEdit = (keyCode) =>{
    if(keyCode === 13){
      dispatch(endEdit(noteLine));
    }
  }

  if(noteLine.isEditing){
    return(
      <textarea value={$lineContents} onKeyDown={event => onEndEdit(event.keyCode)} onChange={e => setLineContents(e)} classname="lineBox"/>
    )
  }else{
    return(
      <button onClick={onBeginEdit} className="NoteLine">
        {noteLine.lineContents} : {noteLine.id}
      </button>
    )
  }
}
