import React, {useEffect,useState} from 'react';
import {useDispatch, useSelector, useStore} from 'react-redux';
import {beginLogin} from './UserActions.js'
export function UserBox(props){

  var userName = props.userName;
  //var noteSheetList = props.noteSheetList;
  const [noteSheetList, setNoteSheetList] = useState(props.noteSheetList);
  //useEffect(() => { setNoteSheetList(props.noteSheetList) }, [props.noteSheetList]);

  const dispatch = useDispatch();

const processesKeyPress = (keyCode) =>{
  if(keyCode === 13){
    onLogin();
  }
}

const onLogin = () =>{
  dispatch(beginLogin(userName))
}

  if(userName == null || userName === ""){
    return(
      <span className="loginBox">
        <input className="inputBox" onChange={e => userName = e.target.value} onKeyDown={event => processesKeyPress(event.keyCode)}/>
        <button className="loginButton" onClick={onLogin}>Login</button>
      </span>

    )
  }else{
    return(
      <span>{ noteSheetList.map(sheet => <div className="noteSheetName">{sheet.noteSheetName}</div>)} </span>
    )
  }
}
