import {Action} from './LineActions';
const initialState = {
  noteLines: [],
  currentLine: {lineContents:"temp"}
}
function reducer(state = initialState, action){
  switch (action.type) {


    case Action.BeginEdit:
      return{
         ...state, currentLine:action.payload,
         noteLines: state.noteLines.map(line =>{
           if(line.id === action.payload.id){
             return{...line, isEditing:true};
           }
           else{
             return line;
           }
         })
       }


    case Action.EndEdit:
      return{
         ...state,  currentLine:{lineContents:"temp"},
         noteLines: state.noteLines.map(line =>{
           if(line.id === action.payload.id){
             alert(linelineContents)
             return{...line,lineContents:action.payload.lineContents, isEditing:false};
           }
           else{
             return line;
           }
         })
       }


     case Action.LoadLine:
       return{
         ...state,
         noteLines: [action.payload, ...state.noteLines],
       }


    default:
      return state;
  }
}
export default reducer;
