import React, {useEffect} from 'react';
import './App.css';
import {useDispatch,useSelector} from 'react-redux';
import {NoteLine} from './NoteLine';
import {loadLine} from './LineActions'

document.addEventListener('keydown', logKey);
function logKey(e) {
   if (e.isComposing || e.keyCode === 13) {
  window.alert();
}
}

function App() {


  const noteLines = useSelector(state => state.noteLines);
  const currentLine = useSelector(state => state.currentLine);
  const dispatch = useDispatch();

  useEffect(() =>{
    dispatch(loadLine({id: 1,lineContents:"placeholder text of somesort"}))
  },[dispatch]);
  return (
    <div className="App">

      <div className="header">
        <div className="title"> Note-Tex </div>
      </div>

      <div className="middle">

        <div className="leftMargin">
          {currentLine.lineContents}
        </div>

        <div className="body">
          {noteLines.map(line => <NoteLine key={line.id} noteLine={line}/>)}
        </div>

        <div className="rightMargin">
          placeholder
        </div>

      </div>

    </div>
  );
}

export default App;
