import React, {useState} from 'react';
import {useDispatch} from 'react-redux';
import {beginEdit} from './LineActions'

export function NoteLine(props){
  const noteLine = props.noteLine
  const dispatch = useDispatch();

  const onEdit = () =>{
    dispatch(beginEdit(noteLine));
  }

  let lineContents = "Next Line...";
  if(noteLine.isEditing){
    return(
      <textarea classname="lineBox"/>
    )
  }else{
    return(
      <div onClick="onEdit" className="NoteLine">
        {lineContents}
      </div>
    )
  }
}
