import React, {useState} from 'react';
import {useDispatch} from 'react-redux';
import {beginEdit} from './LineActions'

export function NoteLine(props){
  const noteLine = props.noteLine;
  const dispatch = useDispatch();
  const lineContents = useState(noteLine.lineContents);

  const onEdit = () =>{
    dispatch(beginEdit(noteLine));
  }

  if(noteLine.isEditing){
    return(
      <textarea classname="lineBox"/>
    )
  }else{
    return(
      <button onClick={onEdit} className="NoteLine">
        {noteLine.lineContents} : {noteLine.id}
      </button>
    )
  }
}
